clear_store.handler:
async function clear_store(args, ctx) {
  const { databaseName, storeName } = args;

  try {
    const request = indexedDB.open(databaseName);

    request.onsuccess = (event) => {
      const db = event.target.result;
      const transaction = db.transaction([storeName], 'readwrite');
      const store = transaction.objectStore(storeName);
      const clearRequest = store.clear();

      clearRequest.onsuccess = () => {
        ctx.triggerActionEvent('success', {
          message: 'Store cleared successfully',
          storeName: storeName
        });
        db.close();
      };

      clearRequest.onerror = () => {
        ctx.triggerActionEvent('error', {
          message: 'Failed to clear store',
          error: clearRequest.error
        });
        db.close();
      };
    };

    request.onerror = () => {
      ctx.triggerActionEvent('error', {
        message: 'Failed to open database',
        error: request.error
      });
    };
  } catch (error) {
    ctx.triggerActionEvent('error', {
      message: 'Exception during clear operation',
      error: error.message
    });
  }
}

