async function delete_database(args, ctx) {
  const { databaseName } = args;

  try {
    const request = indexedDB.deleteDatabase(databaseName);

    request.onsuccess = () => {
      ctx.triggerActionEvent('success', {
        message: 'Database deleted successfully',
        databaseName: databaseName
      });
    };

    request.onerror = () => {
      ctx.triggerActionEvent('error', {
        message: 'Failed to delete database',
        error: request.error
      });
    };

    request.onblocked = () => {
      ctx.triggerActionEvent('blocked', {
        message: 'Database deletion blocked - close all connections first',
        databaseName: databaseName
      });
    };
  } catch (error) {
    ctx.triggerActionEvent('error', {
      message: 'Exception during database deletion',
      error: error.message
    });
  }
}

