init_indexed_db.handler:
async function init_indexed_db(args, ctx) {
  const { databaseName, version, stores } = args;

  try {
    const request = indexedDB.open(databaseName, version);

    request.onupgradeneeded = (event) => {
      const db = event.target.result;

      stores.forEach(store => {
        if (!db.objectStoreNames.contains(store.name)) {
          db.createObjectStore(store.name, {
            keyPath: store.keyPath || 'id',
            autoIncrement: store.autoIncrement !== false
          });
        }
      });
    };

    request.onsuccess = (event) => {
      const db = event.target.result;
      ctx.triggerActionEvent('success', {
        message: 'Database initialized successfully',
        databaseName: db.name,
        version: db.version
      });
      db.close();
    };

    request.onerror = (event) => {
      ctx.triggerActionEvent('error', {
        message: 'Failed to initialize database',
        error: event.target.error
      });
    };
  } catch (error) {
    ctx.triggerActionEvent('error', {
      message: 'Exception during database initialization',
      error: error.message
    });
  }
}

