// Erwartete args: { databaseName: string, storeName: string, items: any[] }
async function add_items_to_store(args, ctx) {
  const { databaseName, storeName, items } = args;

  if (!Array.isArray(items)) {
    ctx.triggerActionEvent('error', { message: 'Invalid input: "items" muss ein Array sein.' });
    return;
  }

  try {
    const request = indexedDB.open(databaseName);

    request.onsuccess = (event) => {
      const db = event.target.result;

      if (items.length === 0) {
        ctx.triggerActionEvent('success', {
          message: 'Keine Items Ã¼bergeben. Nichts geÃ¤ndert.',
          ids: [],
          results: []
        });
        db.close();
        return;
      }

      const transaction = db.transaction([storeName], 'readwrite');
      const store = transaction.objectStore(storeName);
      const keyPath = store.keyPath || null;

      const results = []; // { index, id }
      const errors = [];  // { index, error }

      items.forEach((item, index) => {
        const req = store.put(item); // <- upsert
        req.onsuccess = () => {
          const id = (req.result !== undefined && req.result !== null)
            ? req.result                    // autoIncrement-Keys oder explizite SchlÃ¼ssel bei keyless Stores
            : (keyPath ? item?.[keyPath] : undefined); // fallback bei keyPath
          results.push({ index, id });
        };
        req.onerror = () => {
          errors.push({
            index,
            error: req.error ? req.error.message : 'Unbekannter Fehler beim Upsert'
          });
        };
      });

      transaction.oncomplete = () => {
        if (errors.length === 0) {
          ctx.triggerActionEvent('success', {
            message: `${results.length} Item(s) erfolgreich eingefÃ¼gt/aktualisiert.`,
            ids: results.map(r => r.id),
            results
          });
        } else if (results.length > 0) {
          ctx.triggerActionEvent('partial_success', {
            message: `${results.length} upserted, ${errors.length} fehlgeschlagen.`,
            ids: results.map(r => r.id),
            results,
            errors
          });
        } else {
          ctx.triggerActionEvent('error', {
            message: `Alle ${errors.length} Upserts fehlgeschlagen.`,
            errors
          });
        }
        db.close();
      };

      transaction.onerror = () => {
        ctx.triggerActionEvent('error', {
          message: 'Transaktion fehlgeschlagen.',
          error: transaction.error ? transaction.error.message : 'Unbekannter Transaktionsfehler'
        });
        db.close();
      };

      transaction.onabort = () => {
        ctx.triggerActionEvent('error', {
          message: 'Transaktion abgebrochen.',
          error: transaction.error ? transaction.error.message : 'Unbekannter Abbruchfehler'
        });
        db.close();
      };
    };

    request.onerror = () => {
      ctx.triggerActionEvent('error', {
        message: 'Datenbank konnte nicht geÃ¶ffnet werden.',
        error: request.error ? request.error.message : 'Unbekannter Verbindungsfehler'
      });
    };
  } catch (error) {
    ctx.triggerActionEvent('error', {
      message: 'Exception wÃ¤hrend der Upsert-Operation.',
      error: error?.message || String(error)
    });
  }
}

